function numberFormat(i){return new Intl.NumberFormat("id-ID").format(i)}function parseToNum(i){return parseFloat(i.toString().replace(/,/g,""))}function showCovidData(i,o){const a=o.id===regions.indonesia.id?"#covid-nasional":"#covid-provinsi";Object.keys(o.attributes).forEach(function(n){let s=i[o.attributes[n]],r="wilayah"===n?s.toUpperCase():numberFormat(parseToNum(s));$(a).find(`[data-name=${n}]`).html(`${r}`)}),$(a).find(".shimmer").removeClass("shimmer")}function showError(i=""){$(`${i} .shimmer`).html('<span class="small"><i class="fa fa-exclamation-triangle"></i> Gagal memuat...</span>'),$(`${i} .shimmer`).removeClass("shimmer")}const regions={indonesia:{id:1,attributes:{wilayah:"name",positif:"jumlahKasus",meninggal:"meninggal",sembuh:"sembuh"}},provinsi:{id:2,attributes:{wilayah:"provinsi",positif:"kasusPosi",meninggal:"kasusMeni",sembuh:"kasusSemb"}}};$(document).ready(function(){if($("#covid-nasional").length){const i="https://indonesia-covid-19.mathdro.id/api/",o="provinsi/";try{$.ajax({async:!0,cache:!0,url:i,success:function(i){const o=i;o.name="Indonesia",showCovidData(o,regions.indonesia)},error:function(i){showError("#covid-nasional")}})}catch(i){showError("#covid-nasional")}if(KODE_PROVINSI)try{$.ajax({async:!0,cache:!0,url:i+o,success:function(i){const o=i.data.filter(i=>i.kodeProvi==KODE_PROVINSI);o.length?showCovidData(o[0],regions.provinsi):showError("#covid-provinsi")},error:function(i){showError("#covid-provinsi")}})}catch(i){showError("#covid-provinsi")}}});